plugins {
    id("org.jetbrains.kotlin.jvm") version "1.5.31"
    id("org.jetbrains.dokka") version "1.5.0"
    id("com.github.johnrengelman.shadow") version "7.0.0"
    `java-library`
}

repositories {
    mavenCentral()
    maven {
        url = uri("https://jitpack.io")
    }
}

dependencies {
    // used internally, not exposed to consumers on their own compile classpath
    // Align versions of all Kotlin components
    implementation(platform("org.jetbrains.kotlin:kotlin-bom"))
    implementation("org.jetbrains.kotlin:kotlin-stdlib-jdk8")

    //compileOnly("org.processing:core:3.3.7")
    //compileOnly("org.processing:pde:3.3.7")
    compileOnly("com.github.micycle1:processing-core-4:4.0.1")

    compileOnly("net.java.dev.jna:jna:5.12.1")
    compileOnly("net.java.dev.jna:jna-platform:5.12.1")

    // Test
    testImplementation("org.jetbrains.kotlin:kotlin-test")
    testImplementation("org.jetbrains.kotlin:kotlin-test-junit")
    testImplementation("org.processing:core:3.3.7")

    // Documentation
    dokkaHtmlPlugin("org.jetbrains.dokka:kotlin-as-java-plugin:1.7.10")

    // Exported to consumers
    //api("org.apache.commons:commons-math3:3.6.1")
}

tasks.jar {
    manifest {
        attributes(
            mapOf(
                "Implementation-Title" to project.name,
                "Implementation-Version" to project.version
            )
        )
    }
}

tasks.shadowJar {
    val p5LibrariesFolder: String by project
    val jarName = "${rootProject.name}.jar"
    archiveFileName.set(jarName)
    doLast {
        copy {
            from(layout.buildDirectory.dir("libs/$jarName"))
            into("$p5LibrariesFolder/${rootProject.name}/library/")
        }
        // call zip... to make a zip file with the library including examples,
        // docs, etc
    }
}